    onComboboxChange: function(field, newValue, oldValue, eOpts) {
        var store;
        if(newValue == "auto") {
            store = Ext.create('Ext.data.Store', {
                fields: ['text'],
                data: [
                    {text: "auto"},
                    {text: "10"},
                    {text: "100"},
                    {text: "1000"}
                ]
            });
        } else {
            store = Ext.create('Ext.data.Store', {
                fields: ['text'],
                data: [
                    {text: "10"},
                    {text: "100"},
                    {text: "1000"}
                ]
            });
        }

        var speed = field.up('form').getForm().findField('speed');
        speed.bindStore(store);
        if(newValue !== "auto" && speed.getValue() === "auto") {
            speed.setValue("");
        }
    },

    onPanelShow: function(component, eOpts) {
        vmConstants.me.activeNicCheckbox(component.down('#bondingNICGroup'), Ext.getCmp("viewBondingForm").getEl());
    }

